import React, { useState, useCallback } from 'react';

import './App.css';
import DemoList from './components/Demo/DemoList';
import Button from './components/UI/Button/Button';

function App() {
  const [listTitle, setListTitle] = useState('My List');
  const [listItems, setListItems] = useState([5, 3, 1, 10, 9]);
  const [newItem, setNewItem] = useState(0);

  // WHEN TITLE CHANGES
  // Buttons are not re-rendered 
  // DemoList is re-rendered but not sorted again
  // Sorting happens only when new number is inserted to list
  const changeTitleHandler = useCallback(() => {
    setListTitle('New Title' + Math.floor(Math.random() * 10) + 1);
  }, []);

  const addNumberToList = useCallback(() => {
    setListItems((items) => [...items, newItem])
  }, []);


  return (
    <div className="app">
      <DemoList title={listTitle} items={listItems} />
      <Button onClick={changeTitleHandler}>Change List Title</Button>
      <div style={{margin: '10px'}}>
        <h3>Add new number</h3>
        <input onChange={(e) => setNewItem(e.target.value)} />
        <div style={{margin: '5px'}}>
          <Button onClick={addNumberToList}>Add</Button>
        </div>
      </div>
    </div>
  );
}

export default App;
